<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class item extends CI_Controller{	public function __construct(){	    parent::__construct();	    is_logged_in();	    $this->load->model('M_item');	}	public function index(){				$data = array();		$data['warehousename'] = $this->M_item->Getwarehouse();		$data['suppliername'] = $this->M_item->Getsupplier();		$data['itemname'] = $this->M_item->Getitem();		$this->load->helper(array('form'));	    $this->load->library('form_validation');		if($_SERVER['REQUEST_METHOD'] == 'POST')		{						$this->form_validation->set_rules('item_name', 'item_name', 'required');			$this->form_validation->set_rules('item_price', 'item_price', 'required');			$this->form_validation->set_rules('sku', 'sku', 'required');			if($this->form_validation->run() == true)	   		{				$this->M_item->addpage();				$this->session->set_flashdata('msg', 'Record added successfully!');				redirect('admin/item/listing', 'refresh');			}	 	}	 	//pr($data);die;	   	$this->load->view('item/add_view',$data);	}			function listing($offset=0){		$this->load->helper('form');		$limit = pagination_count;		//echo $limit;		$data['datarows'] = $this->M_item->listing($limit,$offset);		$config['total_rows'] = $this->M_item->count_all();		$this->load->library('pagination');		$config['first_url'] = site_url("admin/item/listing/0/");		$config['base_url'] = site_url("admin/item/listing/");		$config['per_page'] = $limit; 		$config['uri_segment'] = 4;		$this->pagination->initialize($config);		$data["links"] = $this->pagination->create_links();		$data['page'] = $offset;		/*******/		$this->load->view('item/view_list',$data);	}			function edit($id)	{		$data_title=array();		$data = array();		$data['warehousename'] = $this->M_item->Getwarehouse();		$data['suppliername'] = $this->M_item->Getsupplier();		$data['itemname'] = $this->M_item->Getitem();		$this->load->library('form_validation');		if(!empty($_POST)){			$this->form_validation->set_rules('item_name', 'Title', 'required');		}				if($this->form_validation->run() == true)		{			$msg = $this->M_item->index($id,$data_title);			if(!empty($msg)){			   $this->session->set_flashdata('msg', 'Record Updated successfully');			}			redirect('/admin/item/listing/'.$page.'/');		}else{			$data['datarows'] = $this->M_item->page_info($id);			/*echo "<pre>";print_r($data);die;*/			$this->load->view('item/edit_view',$data);		}	}			function delete($pageid){		if($this->M_item->delete($pageid))			$this->session->set_flashdata('msg', 'Record Deleted successfully');		redirect('/admin/item/listing/','refresh');	}			function status($id,$status){		if($this->M_item->status($id,$status))			$this->session->set_flashdata('msg', 'Record Updated successfully');		redirect('/admin/item/listing/','refresh');	}			function ajaxDelete(){		$id = @$_POST['id'];		if($id){			$this->M_item->DeleteStatus($id,0);		}	}			function priority()	{		$id=$_GET['id'];		$priority=$_GET['p'];		$this->m_item->Updatepriority($id,$priority);	}			}