<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class supplier extends CI_Controller{	public function __construct(){	    parent::__construct();	    is_logged_in();	    $this->load->model('m_supplier');	}	public function index(){		$data = array();		$this->load->helper(array('form'));	    $this->load->library('form_validation');		if($_SERVER['REQUEST_METHOD'] == 'POST')		{			//pr($_POST);die;			$this->form_validation->set_rules('supplier_name', 'supplier_name', 'required');			$this->form_validation->set_rules('email_id', 'email_id', 'required');			$this->form_validation->set_rules('city', 'city', 'required');			$this->form_validation->set_rules('state', 'state', 'required');			$this->form_validation->set_rules('country', 'country', 'required');			if($this->form_validation->run() == true)	   		{				$this->m_supplier->addpage();				$this->session->set_flashdata('msg', 'Record added successfully!');				redirect('admin/supplier/listing', 'refresh');			}	 	}	 	$data['countries'] = getCountries();	 	//pr($data);die;	   	$this->load->view('supplier/add_view',$data);	}			function listing($offset=0){		$this->load->helper('form');		$limit = pagination_count;		//echo $limit;		$data['datarows'] = $this->m_supplier->listing($limit,$offset);		$config['total_rows'] = $this->m_supplier->count_all();				/* pagination start */		$this->load->library('pagination');		$config['first_url'] = site_url("admin/supplier/listing/0/");		$config['base_url'] = site_url("admin/supplier/listing/");		$config['per_page'] = $limit; 		$config['uri_segment'] = 4;		$this->pagination->initialize($config);		$data["links"] = $this->pagination->create_links();		$data['page'] = $offset;		/*******/		$this->load->view('supplier/view_list',$data);	}			//edit pages	function edit($id)	{		$data_title=array();		$data = array();		$this->load->library('form_validation');		if(!empty($_POST)){			$this->form_validation->set_rules('supplier_name', 'Title', 'required');		}				if($this->form_validation->run() == true)		{			$msg = $this->m_supplier->index($id,$data_title);			if(!empty($msg)){			   $this->session->set_flashdata('msg', 'Record Updated successfully');			}			redirect('/admin/supplier/listing/'.$page.'/');		}else{			$data['datarows'] = $this->m_supplier->page_info($id);			$country_id = isset($data['datarows'][0]['country'])?$data['datarows'][0]['country']:0;			$state_id = isset($data['datarows'][0]['state'])?$data['datarows'][0]['state']:0;			$city_id = isset($data['datarows'][0]['city'])?$data['datarows'][0]['city']:0;			$data['countries'] = getCountries();			$data['states'] = getStates($country_id);			$data['cities'] = getCities($state_id);			//echo "<pre>";print_r($data);die;			$this->load->view('supplier/edit_view',$data);		}	}			function delete($pageid){		if($this->m_supplier->delete($pageid))			$this->session->set_flashdata('msg', 'Record Deleted successfully');		redirect('/admin/supplier/listing/','refresh');	}		function ajaxDelete(){		$id = @$_POST['id'];		if($id){			$this->m_supplier->DeleteStatus($id,0);		}	}				function status($id,$status){		if($this->m_supplier->status($id,$status))			$this->session->set_flashdata('msg', 'Record Updated successfully');		redirect('/admin/supplier/listing/','refresh');	}			function priority()	{		$id=$_GET['id'];		$priority=$_GET['p'];		$this->m_supplier->Updatepriority($id,$priority);	}}