<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class warehouse extends CI_Controller{	public function __construct(){	    parent::__construct();	    is_logged_in();	    $this->load->model('m_warehouse');	}	public function index(){				$data = array();		$data['companies'] = $this->m_warehouse->GetCompanies();		$this->load->helper(array('form'));	    $this->load->library('form_validation');		if($_SERVER['REQUEST_METHOD'] == 'POST')		{			$this->form_validation->set_rules('name', 'name', 'required');			$this->form_validation->set_rules('email_id', 'email_id', 'required');			$this->form_validation->set_rules('city', 'city', 'required');			$this->form_validation->set_rules('state', 'state', 'required');			$this->form_validation->set_rules('location', 'location', 'required');			$this->form_validation->set_rules('country', 'country', 'required');			//$this->form_validation->set_rules('company_name', 'company_name', 'required');			//$this->form_validation->set_rules('description', 'Description', 'required');			if($this->form_validation->run() == true)	   		{				$this->m_warehouse->addpage();				$this->session->set_flashdata('msg', 'Record added successfully!');				redirect('admin/warehouse/listing', 'refresh');			}	 	}	 	//pr($data);die;	   	$this->load->view('warehouse/add_view',$data);	}			function listing($offset=0){		$this->load->helper('form');		$limit = pagination_count;		//echo $limit;		$data['datarows'] = $this->m_warehouse->listing($limit,$offset);		$config['total_rows'] = $this->m_warehouse->count_all();				/* pagination start */		$this->load->library('pagination');		$config['first_url'] = site_url("admin/warehouse/listing/0/");		$config['base_url'] = site_url("admin/warehouse/listing/");		$config['per_page'] = $limit; 		$config['uri_segment'] = 4;		$this->pagination->initialize($config);		$data["links"] = $this->pagination->create_links();		$data['page'] = $offset;		/*******/		$this->load->view('warehouse/view_list',$data);	}			//edit pages	function edit($id)	{		$data_title=array();		$data = array();		$data['companies'] = $this->m_warehouse->GetCompanies();				$this->load->library('form_validation');		if(!empty($_POST)){			$this->form_validation->set_rules('email_id', 'email_id', 'required');		}				if($this->form_validation->run() == true)		{			$msg = $this->m_warehouse->index($id,$data_title);			if(!empty($msg)){			   $this->session->set_flashdata('msg', 'Record Updated successfully');			}			redirect('/admin/warehouse/listing/'.$page.'/');		}else{			//$data['gallery'] = gethandoverCategories();			$data['datarows'] = $this->m_warehouse->page_info($id);			//echo "<pre>";print_r($data);die;			$this->load->view('warehouse/edit_view',$data);		}	}			function delete($pageid){		if($this->m_warehouse->delete($pageid))			$this->session->set_flashdata('msg', 'Record Deleted successfully');		redirect('/admin/warehouse/listing/','refresh');	}	function ajaxDelete(){		$id = @$_POST['id'];		if($id){			$this->m_warehouse->DeleteStatus($id,0);		}	}			function status($id,$status){		if($this->m_warehouse->status($id,$status))			$this->session->set_flashdata('msg', 'Record Updated successfully');		redirect('/admin/warehouse/listing/','refresh');	}			function priority()	{		$id=$_GET['id'];		$priority=$_GET['p'];		$this->m_warehouse->Updatepriority($id,$priority);	}}