<?php class m_company extends CI_Model{	private $tbl_name = 'tbl_company';	function addpage()	{		$priority = $this->get_length();		/////		$slug = url_title($this->input->post('company_name'), 'dash', true);		$serviceslug = $this->checkSlugExists($slug);		$uid = getCurUserID();        $uDetails = getAdminUserDetailsById($uid);        $usernameAdmin = isset($uDetails['full_name'])?$uDetails['full_name']:$uDetails['username'];		$data = array(			'slug' => $serviceslug,			'company_code' => $this->input->post('company_code'),			'company_name' => $this->input->post('company_name'),			'address1' => $this->input->post('address1'),			'address2' => $this->input->post('address2'),			'contact_person' => $this->input->post('contact_person'),			'email_id' => $this->input->post('email_id'),			'phone' => $this->input->post('phone'),			'landline' => $this->input->post('landline'),			'city' => $this->input->post('city'),			'state' => $this->input->post('state'),			'pincode' => $this->input->post('pincode'),			'gst_no' => $this->input->post('gst_no'),			'currency' => $this->input->post('currency'),			'country' => $this->input->post('country'),			'note' => $this->input->post('note'),			'is_active' => 1,			'delete_status' => 1,			'priority' => $priority,		);		//echo'<pre>';print_r($data);echo'</pre>';die;		$msg = 0;		$this->db->insert($this->tbl_name, $data);		$lastId = $this->db->insert_id();		//echo $this->db->last_query();die;		SaveAuditLog('New Company create', 'tbl_company', $lastId , $usernameAdmin );		$msg = ($this->db->affected_rows() > 0) ? $lastId : 0; //for insert //for insert		return $msg;	}	function checkSlugExists($slug){		$serviceslug = '';		$query = $this->db->query("select * from ".$this->tbl_name." where slug='".$slug."'");		$count = $query->num_rows();		// if slug pre exists		if ($count > 0) {			$id=rand(10000,99999);  			$shorturl=base_convert($id,20,36);			$serviceslug = $slug.$shorturl;			return $serviceslug;		}else		return $slug;	}			function listing($limit,$start)	{		$this->db->select('*');		$this->db->from($this->tbl_name);		$this->db->where('delete_status', 1);		$this->db->limit($limit, $start);		$this->db->order_by('priority','asc');		$query = $this->db->get();		//echo $this->db->last_query();die;		return $query->result_array();	}	function index($id){		$uid = getCurUserID();        $uDetails = getAdminUserDetailsById($uid);        $usernameAdmin = isset($uDetails['full_name'])?$uDetails['full_name']:$uDetails['username'];		$data = array(			'company_code' => $this->input->post('company_code'),			'company_name' => $this->input->post('company_name'),			'address1' => $this->input->post('address1'),			'address2' => $this->input->post('address2'),			'contact_person' => $this->input->post('contact_person'),			'email_id' => $this->input->post('email_id'),			'phone' => $this->input->post('phone'),			'landline' => $this->input->post('landline'),			'city' => $this->input->post('city'),			'state' => $this->input->post('state'),			'pincode' => $this->input->post('pincode'),			'gst_no' => $this->input->post('gst_no'),			'currency' => $this->input->post('currency'),			'country' => $this->input->post('country'),		);		//echo'<pre>';print_r($usernameAdmin);echo'</pre>';die;		$msg = 0;		if( $id == 0 ){			$this->db->insert($this->tbl_name, $data);			$msg = ($this->db->affected_rows() > 0) ? 1 : 0;//for insert		}else{			$this->db->where('id',$id );		    $this->db->update($this->tbl_name, $data);			//echo $this->db->last_query();die;			SaveAuditLog('Company Update', 'tbl_company', $id , $usernameAdmin );			$msg = ($this->db->affected_rows() > 0) ? 2 : 3;//for update : for nothing happened		}		return $msg;	}	function delete($pageid){		$this->db->delete($this->tbl_name, array('id' => $pageid));		return ($this->db->affected_rows() != 1) ? false : true;  	}		function DeleteStatus($id,$DeleteStatus){		$uid = getCurUserID();        $uDetails = getAdminUserDetailsById($uid);        $usernameAdmin = isset($uDetails['full_name'])?$uDetails['full_name']:$uDetails['username'];		$this->db->where('id',$id );        $this->db->set("delete_status", $DeleteStatus);        $this->db->update($this->tbl_name);		//echo $this->db->last_query();die;		SaveAuditLog('Delete Company', 'tbl_company', $id , $usernameAdmin );		return ($this->db->affected_rows() > 0) ? 2 : 0;//for insert // for update  	}			function status($id,$status){		$uid = getCurUserID();        $uDetails = getAdminUserDetailsById($uid);        $usernameAdmin = isset($uDetails['full_name'])?$uDetails['full_name']:$uDetails['username'];		$this->db->where('id',$id );        $this->db->set("is_active", $status);        $this->db->update($this->tbl_name);		//$this->output->enable_profiler(TRUE);		SaveAuditLog('Status Change', 'tbl_company', $id , $usernameAdmin );		return ($this->db->affected_rows() > 0) ? 2 : 0;//for insert // for update  	}	  	function count_all(){		$this->db->select('*');		$query = $this->db->get($this->tbl_name);		return ($query->num_rows());	}	function page_info($id=0){		$this->db->select('*');		$this->db->from($this->tbl_name);		if(!empty($id)){			$this->db->where('id',$id);		}		$query = $this->db->get();		return $query->result_array();	}	function Updatepriority($id,$priority)	{		$this->db->where('id',$id );        $this->db->set("priority", $priority);        $this->db->update($this->tbl_name);	}	function get_length(){		$this->db->select('*');		$this->db->from($this->tbl_name);		$query = $this->db->get();		return count($query->result_array())+1;	}}?>