js/jquery.tagsinput.min.js"></script><link href="<?php echo base_url();?>assets/admin/css/jquery.tagsinput.css" rel="stylesheet" type="text/css" /><script src="https://cdnjs.cloudflare.com/ajax/libs/bootstrap-3-typeahead/4.0.1/bootstrap3-typeahead.min.js"></script><?php */?><link href="<?php echo base_url();?>assets/admin/css/bootstrap-tokenfield.css" rel="stylesheet" type="text/css" /><script src="<?php echo base_url();?>assets/admin/js/bootstrap-tokenfield.js"></script><script>    $(document).ready(function() {        $("#frmsupplier").validate({            rules: {                supplier_name :{required: true},                email_id :{required: true,email: true},                phone:{required: true,digits: true,minlength: 10,maxlength: 10},                landline:{digits: true,minlength: 10,maxlength: 15},                country :{required: true},                city :{required: true},                state :{required: true},            },            messages: {              supplier_name : "Supplier Name is required.",              phone: {                required: "Please enter your mobile.",                digits: "Please enter digits."              },              landline: {                required: "Please enter your landline.",                digits: "Please enter digits."              },              email_id : "Please enter a valid email address.",              country : "Select Country.",              city : "Select City.",              state : "Select State.",            }        });        /*$('#tagItems').tagsInput({            trimValue: true,allowDuplicates: false,            'defaultText':'Type Item','height':'110px','width':'300px',        });*/        /*$('input.typeahead').typeahead({            source:  function (query, process) {            return $.get('<?php //echo base_url();?>ajax/getitemsbyname', { itemname: query }, function (data) {                    console.log(data);                    data = $.parseJSON(data);                    return process(data);                });            }        });*/                $('input.typeahead').tokenfield({            allowDuplicates: false,            autocomplete: {                source: function (request, response) {                  jQuery.get('<?php echo base_url();?>ajax/getitemsbyname', {                      itemname: request.term                  }, function (data) {                      data = $.parseJSON(data);                      //console.log(data);                      //response(data);                      response($.map(data, function (item) {                        //console.log({ ID: item.id, Name: item.name });                        return { value: item.id, label: item.name };                      }));                  });                },                focus: function( event, ui ) {                    $(this).val(ui.item.label);                    return false;                },                delay: 100            },            showAutocompleteOnFocus: false        });        $("#country").change(function(){            var stateval=$(this).val();            $.ajax({                type:"POST",                async:false,                url: '<?php echo base_url(); ?>ajax/getstate',                data: {id:stateval},                success: function(output){                  $('#state').empty();                  var arroutput = JSON.parse(output);                  //console.log(arroutput);                  var $select = $('#state');                  $('<option>').val('').text('Select State').appendTo($select);                  $.each(arroutput, function(key, value) {                    if(key){                        $('<option>').val(key).text(value).appendTo($select);                    }                  });                }            });        });        $("#state").change(function(){            var stateval=$(this).val();            $.ajax({                type:"POST",                async:false,                url: '<?php echo base_url(); ?>ajax/getcity',                data: {id:stateval},                success: function(output){                  $('#city').empty();                  var arroutput = JSON.parse(output);                  //console.log(arroutput);                  var $select = $('#city');                  $('<option>').val('').text('Select City').appendTo($select);                  $.each(arroutput, function(key, value) {                    if(key){                        $('<option>').val(key).text(value).appendTo($select);                    }                  });                }            });        });    });</script>