ing/'.$page.'/');		}else{			//$data['gallery'] = gethandoverCategories();			$data['datarows'] = $this->m_supplier->page_info($id);			$country_id = isset($data['datarows'][0]['country'])?$data['datarows'][0]['country']:0;			$state_id = isset($data['datarows'][0]['state'])?$data['datarows'][0]['state']:0;			$city_id = isset($data['datarows'][0]['city'])?$data['datarows'][0]['city']:0;			$data['countries'] = getCountries();			$data['states'] = getStates($country_id);			$data['cities'] = getCities($state_id);			//echo "<pre>";print_r($data);die;			$this->load->view('supplier/edit_view',$data);		}	}			function delete($pageid){		if($this->m_supplier->delete($pageid))			$this->session->set_flashdata('msg', 'Record Deleted successfully');		redirect('/admin/supplier/listing/','refresh');	}			function status($id,$status){		if($this->m_company->status($id,$status))			$this->session->set_flashdata('msg', 'Record Updated successfully');		redirect('/admin/supplier/listing/','refresh');	}			function priority()	{		$id=$_GET['id'];		$priority=$_GET['p'];		$this->m_supplier->Updatepriority($id,$priority);	}}