<?php class M_item extends CI_Model{	private $tbl_name = 'tbl_item';	function addpage()	{		$data1 = array();		$priority = $this->get_length();		$banner_image = $this->uploadImageObject($_FILES['profilepic']);		if($banner_image){			$data1 = array('image_upload' => $banner_image);		}		/////		$slug = url_title($this->input->post('item_name'), 'dash', true);		$serviceslug = $this->checkSlugExists($slug);		$data2 = array(			'slug' => $serviceslug,			'item_name' => $this->input->post('item_name'),			'item_code' => $this->input->post('item_code'),			'sku' => $this->input->post('sku'),			'item_id' => $this->input->post('itemname'),			'item_price' => $this->input->post('item_price'),			'whm_id' => $this->input->post('warehousename'),			'manufacturer' => $this->input->post('manufacturer'),			'brand' => $this->input->post('brand'),			'serial_no' => $this->input->post('serial_no'),			'mpn' => $this->input->post('mpn'),			'minimum_quantity' => $this->input->post('minimum_quantity'),			'minimum_threshold' => $this->input->post('minimum_threshold'),			'warranty' => $this->input->post('warranty'),			'distription' => $this->input->post('distription'),			//'quinty' => $this->input->post('quinty'),			//'recive_date' => $this->input->post('recive_date'),			//'status' => $this->input->post('status'),			//'rate' => $this->input->post('rate'),			//'discount' => $this->input->post('discount'),			//'supplier_id' => $this->input->post('suppliername'),			//'item_price' => $this->input->post('item_price'),			'note' => $this->input->post('note'),			'is_active' => 1,			'priority' => $priority,		);		$data = array_merge($data1,$data2);		//echo'<pre>';print_r($data);echo'</pre>';die;		$msg = 0;		$this->db->insert($this->tbl_name, $data);		//echo $this->db->last_query();die;		$msg = ($this->db->affected_rows() > 0) ? 1 : 0; //for insert		return $msg;	}	function checkSlugExists($slug){		$serviceslug = '';		$query = $this->db->query("select * from ".$this->tbl_name." where slug='".$slug."'");		$count = $query->num_rows();		// if slug pre exists		if ($count > 0) {			$id=rand(10000,99999);  			$shorturl=base_convert($id,20,36);			$serviceslug = $slug.$shorturl;			return $serviceslug;		}else		return $slug;	}	//function listing($limit,$start,$startdate,$enddate,$specificOption,$specificValue)	function listing($limit,$start)	{		$this->db->select('*');		$this->db->from($this->tbl_name);		$this->db->limit($limit, $start);		$this->db->order_by('priority','asc');		$query = $this->db->get();		//echo $this->db->last_query();die;		return $query->result_array();	}	function index($id){		$data1 = array();		$priority = $this->get_length();		$banner_image = $this->uploadImageObject($_FILES['profilepic']);		if($banner_image){			$data1 = array('image_upload' => $banner_image);		}		$data2 = array(			'slug' => $serviceslug,			'item_name' => $this->input->post('item_name'),			'item_code' => $this->input->post('item_code'),			'sku' => $this->input->post('sku'),			'item_id' => $this->input->post('itemname'),			'item_price' => $this->input->post('item_price'),			'whm_id' => $this->input->post('warehousename'),			'manufacturer' => $this->input->post('manufacturer'),			'brand' => $this->input->post('brand'),			'serial_no' => $this->input->post('serial_no'),			'mpn' => $this->input->post('mpn'),			'minimum_quantity' => $this->input->post('minimum_quantity'),			'minimum_threshold' => $this->input->post('minimum_threshold'),			'warranty' => $this->input->post('warranty'),			'distription' => $this->input->post('distription'),			//'quinty' => $this->input->post('quinty'),			//'recive_date' => $this->input->post('recive_date'),			//'status' => $this->input->post('status'),			//'rate' => $this->input->post('rate'),			//'discount' => $this->input->post('discount'),			//'supplier_id' => $this->input->post('suppliername'),			//'item_price' => $this->input->post('item_price'),			'note' => $this->input->post('note'),			'is_active' => 1,			'priority' => $priority,		);		// echo'<pre>';print_r($data);echo'</pre>';die;		$data = array_merge($data1,$data2);		$msg = 0;		if( $id == 0 ){			$this->db->insert($this->tbl_name, $data);			$msg = ($this->db->affected_rows() > 0) ? 1 : 0;//for insert		}else{			$this->db->where('id',$id );		    $this->db->update($this->tbl_name, $data);			//echo $this->db->last_query();die;			$msg = ($this->db->affected_rows() > 0) ? 2 : 3;//for update : for nothing happened		}		return $msg;	}	function delete($pageid){		$this->db->delete($this->tbl_name, array('id' => $pageid));		return ($this->db->affected_rows() != 1) ? false : true;  	}	function status($id,$status){		$this->db->where('id',$id );        $this->db->set("is_active", $status);        $this->db->update($this->tbl_name);		//$this->output->enable_profiler(TRUE);		return ($this->db->affected_rows() > 0) ? 2 : 0;//for insert // for update  	}	//function count_all($startdate,$enddate,$specificOption,$specificValue){  	function count_all(){		$this->db->select('*');		/*if(!isEmpty($specificOption) && !isEmpty($specificValue)){		   $this->db->like($specificOption,$specificValue);		}*/		$query = $this->db->get($this->tbl_name);		return ($query->num_rows());	}	function page_info($id=0){		$this->db->select('*');		$this->db->from($this->tbl_name);		if(!empty($id)){			$this->db->where('id',$id);		}		$query = $this->db->get();		return $query->result_array();	}	function Updatepriority($id,$priority)	{		$this->db->where('id',$id );        $this->db->set("priority", $priority);        $this->db->update($this->tbl_name);		//$this->output->enable_profiler(TRUE);		//return ($this->db->affected_rows() > 0) ? 2 : 0;//for insert // for update	}	function get_length(){		$this->db->select('*');		$this->db->from($this->tbl_name);		$query = $this->db->get();		return count($query->result_array())+1;	}		function Getwarehouse(){		$query = $this->db->get('tbl_warehouse');		$role[''] = "Select Warehouse*";		foreach($query->result() as $row){			$role[$row->id] =  ucfirst($row->name);		}		return $role;	}		function Getsupplier(){		$query = $this->db->get('tbl_supplier');		$role[''] = "Select Supplier*";		foreach($query->result() as $row){			$role[$row->id] =  ucfirst($row->supplier_name);		}		return $role;	}		function Getitem(){		$query = $this->db->get('tbl_itemtype');		$role[''] = "Select item*";		foreach($query->result() as $row){			$role[$row->id] =  ucfirst($row->item_type);		}		return $role;	}		public function uploadImageObject($objfile)	{		//echo '<pre>';print_r($objfile);die;		$time = time();		$url = 'assets/item/'.$time."_".$objfile['name'];	 	//extensive suitability check before doing anything with the file...	    if (($objfile == "none") || (empty($objfile['name'])) )	    {	       $message = "No file uploaded.";	       $url  = '';	    }	    else if ($objfile["size"] == 0)	    {	       $message = "The file is of zero length.";$url  = '';	    }	    else if (!is_uploaded_file($objfile["tmp_name"]))	    {	       $message = "You may be attempting to hack our server. We're on to you; expect a knock on the door sometime soon.";	       $url  = '';	    }	    else {	      $message = "";	      $move = @ move_uploaded_file($objfile['tmp_name'], $url);	      if(!$move)	      {	         $message = "Error moving uploaded file. Check the script is granted Read/Write/Modify permissions.";	         $url  = '';	      }	    }	    return $url;	}}?>