<?php class m_purchase extends CI_Model{	private $tbl_name = 'tbl_purchase';	function addpage()	{		$priority = $this->get_length();		/////		$slug = url_title($this->input->post('warehouse_name'), 'dash', true);		$serviceslug = $this->checkSlugExists($slug);		$data = array(			'slug' => $serviceslug,			'item_id' => $this->input->post('itemname'),			'warehouse_id' => $this->input->post('warehousename'),			'supplier_id' => $this->input->post('suppliername'),			'company_id' => $this->input->post('companyname'),			'purchase_date' => $this->input->post('purchase_date'),			'quinty' => $this->input->post('quinty'),			'unit_price' => $this->input->post('unit_price'),			'invoice_no' => $this->input->post('invoice_no'),			'purchase_order' => $this->input->post('purchase_order'),			'note' => $this->input->post('note'),			'order_type' => "Purchase Order",			'is_active' => 1,			'priority' => $priority,		);		//echo'<pre>';print_r($data);echo'</pre>';die;		$msg = 0;		$this->db->insert($this->tbl_name, $data);		//echo $this->db->last_query();die;		$msg = ($this->db->affected_rows() > 0) ? 1 : 0; //for insert		return $msg;	}	function checkSlugExists($slug){		$serviceslug = '';		$query = $this->db->query("select * from ".$this->tbl_name." where slug='".$slug."'");		$count = $query->num_rows();		// if slug pre exists		if ($count > 0) {			$id=rand(10000,99999);  			$shorturl=base_convert($id,20,36);			$serviceslug = $slug.$shorturl;			return $serviceslug;		}else		return $slug;	}	//function listing($limit,$start,$startdate,$enddate,$specificOption,$specificValue)	function listing($limit,$start)	{		$this->db->select('*');		$this->db->from($this->tbl_name);		$this->db->where('order_type','Purchase Order');		$this->db->limit($limit, $start);		$this->db->order_by('priority','asc');		$query = $this->db->get();		//echo $this->db->last_query();die;		return $query->result_array();	}	function index($id){		$data = array(			'item_id' => $this->input->post('itemname'),			'warehouse_id' => $this->input->post('warehousename'),			'supplier_id' => $this->input->post('suppliername'),			'company_id' => $this->input->post('companyname'),			'purchase_date' => $this->input->post('purchase_date'),			'quinty' => $this->input->post('quinty'),			'unit_price' => $this->input->post('unit_price'),			'invoice_no' => $this->input->post('invoice_no'),			'purchase_order' => $this->input->post('purchase_order'),			'note' => $this->input->post('note'),		);		// echo'<pre>';print_r($data);echo'</pre>';die;		$msg = 0;		if( $id == 0 ){			$this->db->insert($this->tbl_name, $data);			$msg = ($this->db->affected_rows() > 0) ? 1 : 0;//for insert		}else{			$this->db->where('id',$id );		    $this->db->update($this->tbl_name, $data);			//echo $this->db->last_query();die;			$msg = ($this->db->affected_rows() > 0) ? 2 : 3;//for update : for nothing happened		}		return $msg;	}	function delete($pageid){		$this->db->delete($this->tbl_name, array('id' => $pageid));		return ($this->db->affected_rows() != 1) ? false : true;  	}	function status($id,$status){		$this->db->where('id',$id );        $this->db->set("is_active", $status);        $this->db->update($this->tbl_name);		//$this->output->enable_profiler(TRUE);		return ($this->db->affected_rows() > 0) ? 2 : 0;//for insert // for update  	}	//function count_all($startdate,$enddate,$specificOption,$specificValue){  	function count_all(){		$this->db->select('*');		/*if(!isEmpty($specificOption) && !isEmpty($specificValue)){		   $this->db->like($specificOption,$specificValue);		}*/		$this->db->where('order_type','Purchase Order');		$query = $this->db->get($this->tbl_name);		return ($query->num_rows());	}	function page_info($id=0){		$this->db->select('*');		$this->db->from($this->tbl_name);		if(!empty($id)){			$this->db->where('id',$id);		}		$query = $this->db->get();		return $query->result_array();	}	function Updatepriority($id,$priority)	{		$this->db->where('id',$id );        $this->db->set("priority", $priority);        $this->db->update($this->tbl_name);		//$this->output->enable_profiler(TRUE);		//return ($this->db->affected_rows() > 0) ? 2 : 0;//for insert // for update	}	function get_length(){		$this->db->select('*');		$this->db->from($this->tbl_name);		$query = $this->db->get();		return count($query->result_array())+1;	}		function Getitem(){		$query = $this->db->get('tbl_item');		$role[''] = "Select item*";		foreach($query->result() as $row){			$role[$row->id] =  ucfirst($row->item_name);		}		return $role;	}		function Getwarehouse(){		$query = $this->db->get('tbl_warehouse');		$role[''] = "Select Warehouse*";		foreach($query->result() as $row){			$role[$row->id] =  ucfirst($row->name);		}		return $role;	}		function Getsupplier(){		$query = $this->db->get('tbl_supplier');		$role[''] = "Select supplier*";		foreach($query->result() as $row){			$role[$row->id] =  ucfirst($row->supplier_name);		}		return $role;	}		function GetCompanies(){		$query = $this->db->get('tbl_company');		$role[''] = "Select Company*";		foreach($query->result() as $row){			$role[$row->id] =  ucfirst($row->company_name);		}		return $role;	}}?>