     $.ajax({                type:"POST",                async:false,                url: '<?php echo base_url(); ?>ajax/getstate',                data: {id:stateval},                success: function(output){                  $('#state').empty();                  var arroutput = JSON.parse(output);                  //console.log(arroutput);                  var $select = $('#state');                  $('<option>').val('').text('Select State').appendTo($select);                  $.each(arroutput, function(key, value) {                    if(key){                        $('<option>').val(key).text(value).appendTo($select);                    }                  });                }            });        });        $("#state").change(function(){            var stateval=$(this).val();            $.ajax({                type:"POST",                async:false,                url: '<?php echo base_url(); ?>ajax/getcity',                data: {id:stateval},                success: function(output){                  $('#city').empty();                  var arroutput = JSON.parse(output);                  //console.log(arroutput);                  var $select = $('#city');                  $('<option>').val('').text('Select City').appendTo($select);                  $.each(arroutput, function(key, value) {                    if(key){                        $('<option>').val(key).text(value).appendTo($select);                    }                  });                }            });        });});</script>